mapper.select:
select * from hotel r INNER JOIN hotel_meta hm ON hm.id =r.hotel_meta_id


	hm.category=#{category}
	and r.location = #{location}
	and r.indexs like  '%${indexs}%'
	and hm.level = #{level}

 order by r.create_time desc

mapper.update:
update
hotel 

	update_time = #{updateTime}
	and name = #{name}
	and description = #{description}
	and type=#{type}
	and picture_path =#{picturePath}
	and price=#{price}
	and status=#{status}
	and location = #{location}
	and indexs = #{indexs}
	and theme_id = #{themeId}

where  id =#{id}
